import { Component, useState } from "react";
import AppInfo from "../app-info/app-info";
import SearchPanel from "../search-panel/search-panel";
import AppFilter from "../app-filter/app-filter";
import MovieList from "../movie-list/movie-list";
import MoviesAddForm from "../movies-add-form/movies-add-form";
import { v4 as uuidv4 } from "uuid";

import "./app.scss";

const App = () => {
  const [data, setData] = useState([
    {
      name: "Empire of osman",
      viewers: 988,
      favourite: false,
      like: false,
      id: 1,
    },
    { name: "Ertugrul", viewers: 789, favourite: false, like: false, id: 2 },
    { name: "Omar", viewers: 1091, favourite: false, like: false, id: 3 },
  ]);
  const [term, setTerm] = useState("");
  const [filter, setFilter] = useState("all");

  const onDelete = (id) => {
    const newData = data.filter((c) => c.id !== id);
    setData(newData);
  };

  const addForm = (item) => {
    const newItem = {
      name: item.name,
      viewers: item.viewers,
      id: uuidv4(),
      favourite: false,
      like: false,
    };
    const newData = [...data, newItem];
    setData(newData);
  };

  const onToggleProp = (id, prop) => {
    const newData = data.map((item) => {
      if (item.id === id) {
        return { ...item, [prop]: !item[prop] };
      }
      return item;
    });
    setData(newData);
  };

  const searchHandler = (arr, term) => {
    if (term.length === 0) {
      return arr;
    }

    return arr.filter((item) => item.name.toLowerCase().indexOf(term) > -1);
  };

  const filterHandler = (arr, filter) => {
    switch (filter) {
      case "popular":
        return arr.filter((c) => c.like);
      case "mostViewers":
        return arr.filter((c) => c.viewers > 800);
      default:
        return arr;
    }
  };

  const updateTermHandler = (term) => setTerm(term);

  const updateFilterHandler = (filter) => setFilter(filter);

  return (
    <div className="app font-monospace">
      <div className="content">
        <AppInfo
          allMoviesCount={allMoviesCount}
          favouriteMovieCount={favouriteMovieCount}
        />
        <div className="search-panel">
          <SearchPanel updateTermHandler={updateTermHandler} />
          <AppFilter
            filter={filter}
            updateFilterHandler={this.updateFilterHandler}
          />
